import React from "react";
import Button from "../../Button/Button";
import {
  Container,
  Header,
  HeaderTitle,
  SortWrapper,
  Main,
  MainHeader,
  MainHeaderTitle,
  OrderList,
  Order,
  OrderHeader,
  OrderInfo,
  OrderName,
  OrderPrice,
  OrderCount,
  OrderTotalPrice,
  OrderFooter,
  Input,
  Delete,
} from "./OrdersElements";

const Orders = ({ ordersList }) => {
  const inputStyle = {
    width: "100%",
    height: "45px",
    border: "none",
    outline: "none",
    background: "transparent",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
    color: "#E0E6E9",
  };

  const iconStyle = {
    fontSize: "20px",
    color: "#EA7C69",
  };

  const imgStyle = {
    borderRadius: "100%",
  };

  React.useEffect(()=> {

  })

  const ordersListPrint = () => {
   const ordersList1 =  ordersList.map((item) => {
      return (
        <Order>
          <OrderHeader>
            <img
              style={imgStyle}
              width="40px"
              height="40px"
              src={item.img}
              alt={item.name}
            />
            <OrderInfo>
              <OrderName>{item.name}</OrderName>
              <OrderPrice>${item.price}</OrderPrice>
            </OrderInfo>
            <OrderCount>0</OrderCount>
            <OrderTotalPrice>$Total</OrderTotalPrice>
          </OrderHeader>
          <OrderFooter>
            <Input>
              <input
                type="text"
                placeholder="Order Note..."
                style={inputStyle}
              />
            </Input>
            <Delete>
              <i style={iconStyle} className="fa fa-trash"></i>
            </Delete>
          </OrderFooter>
        </Order>
      );
    })
    return ordersList1;
  }

  return (
    <Container>
      <Header>
        <HeaderTitle>Orders</HeaderTitle>
        <SortWrapper>
          <Button name="Dine In" width="66px" height="34px" active={true} />
          <Button name="To Go" width="58px" height="34px" active={false} />
          <Button name="Delivery" width="74px" height="34px" active={false} />
        </SortWrapper>
      </Header>
      <Main>
        <MainHeader>
          <MainHeaderTitle>Item</MainHeaderTitle>
          <MainHeaderTitle>Qty</MainHeaderTitle>
          <MainHeaderTitle>Price</MainHeaderTitle>
        </MainHeader>
        <OrderList>
            {ordersListPrint()}
        </OrderList>
      </Main>
    </Container>
  );
};

export default Orders;
