import React, { useState } from "react";
import { titles } from "../../data/data";
import {
  Container,
  Primary,
  Header,
  User,
  UserName,
  UserDate,
  Search,
  Category,
  Title,
  Foods,
  FoodsHeader,
  FoodsTitle,
  Sort,
  FoodsMain,
} from "./HomeElements";
import Food from "../Food/Food";
import { foods } from "../../data/data";

const Home = () => {
  const inputStyle = {
    border: "none",
    outline: "none",
    background: "transparent",
    padding: "10px",
    width: "220px",
    height: "40px",
    fontStyle: "normal",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
  };
  const [foodsData, setFoodsData] = useState(foods.data);
  const [titlesData, setTitlesData] = useState(titles);
  const [activeType, setActiveType] = useState("Hot Dishes");

  const clickTitleHandler = (name) => {
    setTitlesData((prev) => {
      const activeItem = titlesData.filter((item) => {return item.name === name});
      console.log(activeItem);
      const changedActiveItem = { ...activeItem[0], active: true };
      return [...titlesData, changedActiveItem];
    });
  };
  return (
    <Container>
      <Primary>
        <Header>
          <User>
            <UserName>Jaegar Resto</UserName>
            <UserDate>Tuesday, 2Feb 2021</UserDate>
          </User>
          <Search>
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              style={inputStyle}
              type="search"
              placeholder="Search for food, coffe, etc.."
            />
          </Search>
        </Header>
        <Category>
          {titlesData.map((title) => {
            return (
              <Title
                onClick={() => clickTitleHandler(title.name)}
                key={title.id}
                style={title.style}
              >
                {title.name}
              </Title>
            );
          })}
        </Category>
        <Foods>
          <FoodsHeader>
            <FoodsTitle>Choose Dishes</FoodsTitle>
            <Sort>
              <i className="fa-sharp fa-solid fa-chevron-down"></i> Dine in
            </Sort>
          </FoodsHeader>
          <FoodsMain>
            {foodsData.map((food) => {
              if (food.type.includes(activeType)) {
                return (
                  <div key={food.id}>
                    <Food
                      id={food.id}
                      img={food.img}
                      name={food.name}
                      price={food.price}
                      bowl={food.bowl}
                    />
                  </div>
                );
              }
            })}
          </FoodsMain>
        </Foods>
      </Primary>
    </Container>
  );
};

export default Home;
