import React, { useState } from "react";
import { titles } from "../../data/data";
import {
  Container,
  Primary,
  Header,
  User,
  UserName,
  UserDate,
  Search,
  Category,
  Title,
  Foods,
  FoodsHeader,
  FoodsTitle,
  Sort,
  FoodsMain,
} from "./HomeElements";
import Food from "../Food/Food";
import { foods } from "../../data/data";
import "./Home.css";
import { NavLink, Route, Routes } from "react-router-dom";
const Home = () => {
  const inputStyle = {
    border: "none",
    outline: "none",
    background: "transparent",
    padding: "10px",
    width: "220px",
    height: "40px",
    fontStyle: "normal",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
  };
  const [foodsData, setFoodsData] = useState(foods.data);
  const [titlesData, setTitlesData] = useState(titles);
  const [activeType, setActiveType] = useState("Hot Dishes");

  const clickTitleHandler = (name) => {
    setTitlesData((prev) => {
      const newArr = [...prev];
      newArr.forEach((item) => {
        if (item.name === name) {
          item.active = true;
        }
      });
      console.log(newArr);
      return newArr;
    });
    setActiveType(name);
  };

  return (
    <Container>
      <Primary>
        <Header>
          <User>
            <UserName>Jaegar Resto</UserName>
            <UserDate>Tuesday, 2Feb 2021</UserDate>
          </User>
          <Search>
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              style={inputStyle}
              type="search"
              placeholder="Search for food, coffe, etc.."
            />
          </Search>
        </Header>
        <Category>
          {titlesData.map((title) => {
            return (
              <NavLink
                activeClassName="active"
                to="/"
                onClick={() => clickTitleHandler(title.name)}
                key={title.id}
                className="nav-link"
              >
                {title.name}
              </NavLink>
            );
          })}
        </Category>
        <Foods>
          <FoodsHeader>
            <FoodsTitle>Choose Dishes</FoodsTitle>
            <Sort>
              <i className="fa-sharp fa-solid fa-chevron-down"></i> Dine in
            </Sort>
          </FoodsHeader>
          <FoodsMain>
            <Routes>
              {/* {foodsData.map((food) => {
                if (food.type.includes(activeType)) {
                  return (
                    <Route
                      to="/"
                      element={
                        <Food
                          id={food.id}
                          img={food.img}
                          name={food.name}
                          price={food.price}
                          bowl={food.bowl}
                        />
                      }
                    ></Route>
                  );
                }
                return null;
              })} */}
              <Route
                path="/"
                element={
                  <Food
                    id={foodsData[0].id}
                    img={foodsData[0].img}
                    name={foodsData[0].name}
                    price={foodsData[0].price}
                    bowl={foodsData[0].bowl}
                  />
                }
              ></Route>
            </Routes>
          </FoodsMain>
        </Foods>
      </Primary>
    </Container>
  );
};

export default Home;
