import React, { useState } from "react";
import { titles, foods, foodMenu } from "../../../data/data";
import plus from "../../../assets/Plus.svg";
import {
  Container,
  Category,
  Title,
  Foods,
  FoodsHeader,
  FoodsTitle,
  Sort,
  FoodsMain,
  NewDish,
  NewDishIcon,
  NewDishTitle,
  Food,
  FoodName,
  FoodPrice,
  FoodBowl,
  Wrapper,
  Edit,
  EditIcon,
  EditTitle,
  ButtonWrapper,
  Button,
  FoodMenu,
  MenuWrapper,
  MenuNameTitle,
  MenuNameInput,
  TypeWrapper,
  State,
  Type,
  MenuItem,
  MenuButtons,
  MenuButton,
} from "./ProductsElements.js";

const Products = () => {
  const iconStyle = {
    width: "20px",
    height: "20px",
    margin: "0 7px 0",
    textDecoration: "none !important",
  };
  const imgStyle = {
    borderRadius: "50%",
    margin: "0 0 16px",
  };
  const inputStyle = {
    border: "none",
    outline: "none",
    background: "transparent",
    padding: "10px",
    width: "220px",
    height: "40px",
    fontStyle: "normal",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
  };

  const [activeType, setActiveType] = useState("Hot Dishes");
  const [foodsData, setFoodsData] = useState(foods.data);
  const [titlesData, setTitlesData] = useState(titles);
  const [imgInput, setImgInput] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [foodState, setFoodState] = useState(foodMenu.foodState);
  const [foodType, setFoodType] = useState(foodMenu.foodType);
  const [addShow, setAddShow] = useState(true);
  const [btnActive, setBtnActive] = useState(false);

  const styleButton = {
    border: `${btnActive ? "1px solid #EA7C69" : "1x solid #393c49"}`,
    color: `${btnActive ? "1px solid #EA7C69" : "1x solid #393c49"}`,
  };

  const clickTitleHandler = (name, key) => {
    setTitlesData((prevItems) => {
      const updatedItems = [...prevItems];
      const changedItems = updatedItems.map((item) => {
        if (item.name === name) {
          return {
            ...item,
            active: true,
          };
        } else {
          return {
            ...item,
            active: false,
          };
        }
      });
      return changedItems;
    });
    setActiveType(name);
  };

  const imgInputChanged = (e) => {
    setImgInput(e.target.value);
    console.log(e.target.value);
  };

  const nameInputChanged = (e) => {
    setNameInput(e.target.value);
    console.log(e.target.value);
  };

  const foodStateItemClick = (id) => {
    setFoodState((prev) => {
      const data = prev.map((item) => {
        return { ...item, active: false };
      });
      const updatedData = data.map((item) => {
        if (item.id === id) {
          return { ...item, active: true };
        } else {
          return item;
        }
      });
      return updatedData;
    });
  };

  const foodTypeItemClick = (id) => {
    setFoodType((prev) => {
      const data = prev.map((item) => {
        return { ...item, active: false };
      });
      const updatedData = data.map((item) => {
        if (item.id === id) {
          return { ...item, active: true };
        } else {
          return item;
        }
      });
      return updatedData;
    });
  };
  return (
    <Container>
      <FoodsHeader>
        <FoodsTitle>Products Management</FoodsTitle>
        <Sort>
          <i style={iconStyle} className="fa fa-sliders"></i> Manage Categories
        </Sort>
      </FoodsHeader>
      <Category>
        {titlesData.map((title, ind) => {
          return (
            <Title
              key={title.id}
              onClick={() => clickTitleHandler(title.name, ind)}
              style={title.style(title.active)}
            >
              {title.name}
            </Title>
          );
        })}
      </Category>
      <Foods>
        <FoodsMain>
          <NewDish>
            <NewDishIcon>
              <img src={plus} alt="plus" />
            </NewDishIcon>
            <NewDishTitle>Add new dish</NewDishTitle>
          </NewDish>
          {foodsData.map((food) => {
            if (food.type.includes(activeType)) {
              return (
                <Food key={food.id}>
                  <img
                    src={food.img}
                    alt={food.name}
                    width="132px"
                    height="132px"
                    style={imgStyle}
                  />
                  <FoodName>{food.name}</FoodName>
                  <Wrapper>
                    <FoodPrice>${food.price}</FoodPrice> -
                    <FoodBowl>{food.bowl} Bowls</FoodBowl>
                  </Wrapper>
                  <Edit>
                    <EditIcon></EditIcon>
                    <EditTitle>Edit dish</EditTitle>
                  </Edit>
                </Food>
              );
            }
            return null;
          })}
        </FoodsMain>
      </Foods>
      <ButtonWrapper>
        <Button>Discard Changes</Button>
        <Button>Save Changes</Button>
      </ButtonWrapper>
      <FoodMenu>
        <MenuWrapper>
          <MenuNameTitle>Img Url</MenuNameTitle>
          <MenuNameInput>
            <input
              value={imgInput}
              style={inputStyle}
              type="text"
              placeholder="Food img url enter ..."
              onChange={imgInputChanged}
            />
          </MenuNameInput>
        </MenuWrapper>
        <MenuWrapper>
          <MenuNameTitle>Name</MenuNameTitle>
          <MenuNameInput>
            <input
              value={nameInput}
              style={inputStyle}
              type="text"
              placeholder="Food name enter ..."
              onChange={nameInputChanged}
            />
          </MenuNameInput>
        </MenuWrapper>
        <TypeWrapper>
          <State>
            <MenuNameTitle>Food State</MenuNameTitle>
            {foodState.map((item) => (
              <MenuItem
                onClick={() => foodStateItemClick(item.id)}
                key={item.id}
                style={item.style(item.active)}
              >
                {item.type}
              </MenuItem>
            ))}
          </State>
          <Type>
            <MenuNameTitle>Food Type</MenuNameTitle>
            {foodType.map((item) => (
              <MenuItem
                onClick={() => foodTypeItemClick(item.id)}
                key={item.id}
                style={item.style(item.active)}
              >
                {item.type}
              </MenuItem>
            ))}
          </Type>
        </TypeWrapper>
        <MenuButtons>
          <MenuButton style={styleButton}>Cancel</MenuButton>
          {addShow ? (
            <MenuButton style={styleButton}>Add</MenuButton>
          ) : (
            <MenuButton style={styleButton}>Edit</MenuButton>
          )}
        </MenuButtons>
      </FoodMenu>
    </Container>
  );
};

export default Products;
