import React, { useState } from "react";
import { sidebarItems } from "../../data/data";
import { NavLink } from "react-router-dom";
import logo from "../../assets/bx_bxs-store-alt.svg";
import { Items, Item, Wrapper, Logo, Border, Border1 } from "./SidebarElements";

const Sidebar = () => {
  const [data, setData] = useState(sidebarItems.data);
  const itemClickHandler = (id) => {
    setData((data) => {
      const activeItem = data.map((item) => {
        if (item.id === id) {
          return {
            ...item,
            active: true,
          }
        }
      })
      return activeItem;
    });
  };

  return (
    <Items>
      <Logo>
        <img src={logo} alt="logo" />
      </Logo>
      {data.map((item) => (
        <NavLink to={item.link} key={item.id}>
          <Item 
            onClick={() => itemClickHandler(item.id)} 
            style={item.style}
          >
            <Wrapper style={item.wrapperStyle}>
              <i
                style={item.iconStyle}
                className={item.icon}
                aria-hidden="true"
              ></i>
              <Border style={item.style} />
              <Border1 style={item.style} />
            </Wrapper>
          </Item>
        </NavLink>
      ))}
    </Items>
  );
};

export default Sidebar;
