import React, { useState } from "react";
import {
  titles,
  foods,
  foodMenu,
  newFoods,
  Food1,
  newEditFoods,
} from "../../../data/data";
import plus from "../../../assets/Plus.svg";
import {
  Container,
  Category,
  Title,
  Foods,
  FoodsHeader,
  FoodsTitle,
  Sort,
  FoodsMain,
  NewDish,
  NewDishIcon,
  NewDishTitle,
  Food,
  FoodName,
  FoodPrice,
  FoodBowl,
  Wrapper,
  Edit,
  EditIcon,
  EditTitle,
  ButtonWrapper,
  Button,
  FoodMenu,
  MenuWrapper,
  MenuNameTitle,
  MenuNameInput,
  TypeWrapper,
  State,
  Type,
  MenuItem,
  MenuButtons,
  MenuButton,
  Completed,
} from "./ProductsElements.js";

const Products = () => {
  const iconStyle = {
    width: "20px",
    height: "20px",
    margin: "0 7px 0",
    textDecoration: "none !important",
  };
  const imgStyle = {
    borderRadius: "50%",
    margin: "0 0 16px",
  };
  const inputStyle = {
    border: "none",
    outline: "none",
    background: "transparent",
    padding: "10px",
    width: "100%",
    height: "40px",
    fontStyle: "normal",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
  };
  const smallStyle = {
    display: "none",
  };
  const menuStyle = {
    display: "none",
  };

  const [newFoodss, setNewFoods] = useState(newFoods);
  const [newEditFoodss, setNewEditFoods] = useState(newEditFoods);
  const [activeType, setActiveType] = useState("Hot Dishes");
  const [foodsData, setFoodsData] = useState(foods.data);
  const [titlesData, setTitlesData] = useState(titles);
  const [imgInput, setImgInput] = useState("");
  const [nameInput, setNameInput] = useState("");
  const [foodState, setFoodState] = useState(foodMenu.foodState);
  const [foodType, setFoodType] = useState(foodMenu.foodType);
  const [addShow, setAddShow] = useState(true);
  const [menuActive, setMenuActive] = useState(menuStyle);
  const [completed, setCompleted] = useState(smallStyle);
  const [clickedId, setClickedId] = useState("");
  const [buttonActive, setButtonActive] = useState(false);
  const clickTitleHandler = (name, key) => {
    setTitlesData((prevItems) => {
      const updatedItems = [...prevItems];
      const changedItems = updatedItems.map((item) => {
        if (item.name === name) {
          return {
            ...item,
            active: true,
          };
        } else {
          return {
            ...item,
            active: false,
          };
        }
      });
      return changedItems;
    });
    setActiveType(name);
  };

  const imgInputChanged = (e) => {
    setCompleted({ display: "none" });
    setImgInput(e.target.value);
  };

