import React, { useState } from "react";
import {
  Container,
  Primary,
  Header,
  User,
  UserName,
  UserDate,
  Search,
  Category,
  Title,
  Foods,
  FoodsHeader,
  FoodsTitle,
  Sort,
  FoodsMain,
} from "./HomeElements";
import Food from "../Food/Food";
import { foods } from "../../data/data";

const initialState = [
  {
    id: 1453,
    name: "Hot Dishes",
    style: { color: "#EA7C69", borderBottom: "2px solid #EA7C69" },
  },
  {
    id: 14531,
    name: "Cold Dishes",
    style: { color: "#fff", borderBottom: "2px solid transparent" },
  },
  {
    id: 14532,
    name: "Soup",
    style: { color: "#fff", borderBottom: "2px solid transparent" },
  },
  {
    id: 14533,
    name: "Grill",
    style: { color: "#fff", borderBottom: "2px solid transparent" },
  },
  {
    id: 14534,
    name: "Appetizer",
    style: { color: "#fff", borderBottom: "2px solid transparent" },
  },
  {
    id: 14535,
    name: "Dessert",
    style: { color: "#fff", borderBottom: "2px solid transparent" },
  },
];

const Home = () => {
  const inputStyle = {
    border: "none",
    outline: "none",
    background: "transparent",
    padding: "10px",
    width: "220px",
    height: "40px",
    fontStyle: "normal",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
  };
  const [foodsData, setFoodsData] = useState(foods.data);
  const [activeType, setActiveType] = useState("Hot Dishes");
  const [style, setStyle] = useState(initialState);

  const clickTitleHandler = (name, key) => {
    setStyle((prevItems) => {
      const index = key;
      const updatedItems = [...prevItems];
      const changedItems = updatedItems.map((item)=> {
        if (item.name === name) {
          return(
            {
              ...item, 
              style: { color: "#EA7C69", borderBottom: "2px solid #EA7C69" },
            }
          )
        } else {
          return(
            {
              ...item, 
              style: { color: "#fff", borderBottom: "2px solid transparent" },
            }
          )
        }
      })
      return changedItems;
    });
    setActiveType(name);
  };

  return (
    <Container>
      <Primary>
        <Header>
          <User>
            <UserName>Jaegar Resto</UserName>
            <UserDate>Tuesday, 2Feb 2021</UserDate>
          </User>
          <Search>
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              style={inputStyle}
              type="search"
              placeholder="Search for food, coffe, etc.."
            />
          </Search>
        </Header>
        <Category>
          {style.map((title, ind) => {
            return (
              <Title
                key={title.id}
                onClick={() => clickTitleHandler(title.name, ind)}
                style={title.style}
              >
                {title.name}
              </Title>
            );
          })}
        </Category>
        <Foods>
          <FoodsHeader>
            <FoodsTitle>Choose Dishes</FoodsTitle>
            <Sort>
              <i className="fa-sharp fa-solid fa-chevron-down"></i> Dine in
            </Sort>
          </FoodsHeader>
          <FoodsMain>
            {foodsData.map((food) => {
              if (food.type.includes(activeType)) {
                return (
                  <div key={food.id}>
                    <Food
                      id={food.id}
                      img={food.img}
                      name={food.name}
                      price={food.price}
                      bowl={food.bowl}
                    />
                  </div>
                );
              }
              return null;
            })}
          </FoodsMain>
        </Foods>
      </Primary>
    </Container>
  );
};

export default Home;
