import React, { useState } from "react";
import {
  Container,
  Primary,
  Header,
  User,
  UserName,
  UserDate,
  Search,
  Category,
  Title,
  Foods,
  FoodsHeader,
  FoodsTitle,
  Sort,
  FoodsMain,
} from "./HomeElements";
import Food from "../Food/Food";
import { titles, foods, ordersList } from "../../data/data";
import Orders from "./Orders/Orders";

const Home = () => {
  const inputStyle = {
    border: "none",
    outline: "none",
    background: "transparent",
    padding: "10px",
    width: "220px",
    height: "40px",
    fontStyle: "normal",
    fontWeight: "400",
    fontSize: "14px",
    lineHeight: "140%",
  };
  const [foodsData, setFoodsData] = useState(foods.data);
  const [activeType, setActiveType] = useState("Hot Dishes");
  const [titlesData, setTitlesData] = useState(titles);
  const [search, setSearch] = useState("");
  const [orders, setOrders] = useState(ordersList);

  const clickTitleHandler = (name, key) => {
    setSearch("");
    setTitlesData((prevItems) => {
      const updatedItems = [...prevItems];
      const changedItems = updatedItems.map((item) => {
        if (item.name === name) {
          return {
            ...item,
            active: true,
          };
        } else {
          return {
            ...item,
            active: false,
          };
        }
      });
      return changedItems;
    });
    setActiveType(name);
  };

  const clickFoodHandler = (food) => {
    setOrders((prev) => {
      const arr = [...prev];
      const idss = [1];
      arr.forEach((item) => {
        if (!idss.includes(item.id)) {
          console.log("hello");
          arr.unshift(food);
          ids.push(food.id);
        }
      });
      // let uniqueArr = [];
      // let ids = [];
      // arr.forEach((item) => {
      //   if (!ids.includes(item.id)) {
      //     uniqueArr.unshift(item);
      //     ids.push(item.id);
      //   }
      // });
      console.log(arr);
      return arr;
    });

    setOrders((prev) => {
      const updatedData = prev.map((item) => {
        if (item.id === food.id) {
          return { ...item, count: item.count + 1 };
        } else {
          return item;
        }
      });
      console.log(updatedData);
      return updatedData;
    });

    // setFoodsData((prev) => {
    //   const updateData = prev.map((item) => {
    //     if (item.id === food.id) {
    //       return { ...item, count: item.count + 1 };
    //     } else {
    //       return item;
    //     }
    //   });
    //   return updateData;
    // });
  };

  const searchFoodHandler = (e) => {
    setSearch(e.target.value);
  };

  let arr = [
    {
      id: 1,
      name: "a",
    },
    {
      id: 1,
      name: "a",
    },
    {
      id: 2,
      name: "b",
    },
    {
      id: 2,
      name: "b",
    },
  ];

  let uniqueArr = [];
  let ids = [];

  arr.forEach((item) => {
    if (!ids.includes(item.id)) {
      uniqueArr.push(item);
      ids.push(item.id);
    }
  });

  return (
    <Container>
      <Primary>
        <Header>
          <User>
            <UserName>Jaegar Resto</UserName>
            <UserDate>Tuesday, 2Feb 2021</UserDate>
          </User>
          <Search>
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              value={search}
              onChange={searchFoodHandler}
              style={inputStyle}
              type="search"
              placeholder="Search for food, coffe, etc.."
            />
          </Search>
        </Header>
        <Category>
          {titlesData.map((title, ind) => {
            return (
              <Title
                key={title.id}
                onClick={() => clickTitleHandler(title.name, ind)}
                style={title.style(title.active)}
              >
                {title.name}
              </Title>
            );
          })}
        </Category>
        <Foods>
          <FoodsHeader>
            <FoodsTitle>Choose Dishes</FoodsTitle>
            <Sort>
              <i className="fa-sharp fa-solid fa-chevron-down"></i> Dine in
            </Sort>
          </FoodsHeader>
          <FoodsMain>
            {search === ""
              ? foodsData.map((food) => {
                  if (food.type.includes(activeType)) {
                    return (
                      <div key={food.id} onClick={() => clickFoodHandler(food)}>
                        <Food
                          id={food.id}
                          img={food.img}
                          name={food.name}
                          price={food.price}
                          bowl={food.bowl}
                        />
                      </div>
                    );
                  }
                  return null;
                })
              : foodsData.map((food) => {
                  if (food.name.toLowerCase().includes(search)) {
                    return (
                      <div key={food.id} onClick={() => clickFoodHandler(food)}>
                        <Food
                          id={food.id}
                          img={food.img}
                          name={food.name}
                          price={food.price}
                          bowl={food.bowl}
                        />
                      </div>
                    );
                  }
                  return null;
                })}
          </FoodsMain>
        </Foods>
      </Primary>
      <Orders foodsData={orders} setFoodsData={setOrders} />
    </Container>
  );
};

export default Home;
