import React, { useState } from "react";
import { sidebarItems } from "../../data/data";
import { Link } from "react-router-dom";
import logo from "../../assets/bx_bxs-store-alt.svg";
import { Items, Item, Wrapper, Logo, Border, Border1 } from "./SidebarElements";

const Sidebar = () => {
  const [data, setData] = useState(sidebarItems.data);
  const itemClickHandler = id => {
    setData(()=>{
      data.forEach((item) => {
        item.active = false;
      })
    })
  }

  return (
    <Items>
      <Logo>
        <img src={logo} alt="logo" />
      </Logo>
      {data.map((item) => (
        <Link to={item.link} key={item.id}>
          <Item onClick={() => itemClickHandler(item.id)} style={item.style}>
            <Wrapper style={item.wrapperStyle}>
              <i style={item.iconStyle} className={item.icon} aria-hidden="true"></i>
              <Border style={item.style} />
              <Border1 style={item.style} />
            </Wrapper>
          </Item>
        </Link>
      ))}
    </Items>
  );
};

export default Sidebar;
